from aiogram import types, md
from aiogram.dispatcher.webhook import SendMessage
from dispatcher import dp
import config
from datetime import datetime


async def helpComand(message: types.Message):

    await message.answer(md.text(
        md.bold('–°–ø–∏—Å–æ–∫ –∫–æ–º–∞–Ω–¥ –î–µ–¥–∞-–±–æ—Ç–∞:'),
        md.text('üî∏', md.bold('/–∏–Ω—Ñ–æ')),
        md.text('üî∏', md.bold('/–∏–Ω—Ñ–∞')),
        md.text('üî∏', md.bold('–ø—Ä–∏–≤–µ—Ç, –¥–µ–¥—ã')),
        md.text('üî∏', md.bold('–∞–ª–∏—Å–∞, –∫–æ—Ç–æ—Ä—ã–π —á–∞—Å')),
        sep='\n',
    ))


@dp.message_handler()
async def echo(message: types.Message):

    locale = message.from_user.locale
    loc2 = message.from_user.id

    match message.text.lower():
        case "/–∏–Ω—Ñ–æ" | "/info" | "/information":
            await message.answer("https://github.com/OldCodersClub/faq")
        case "/–Ω–æ–º–µ—Ç–∞" | "/nometa":
            await message.answer("\n[–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –Ω–µ –∑–∞–¥–∞–≤–∞–π—Ç–µ –º–µ—Ç–∞-–≤–æ–ø—Ä–æ—Å–æ–≤ –≤ —á–∞—Ç–µ!](https://nometa.xyz/)", parse_mode='markdown')
        case "/get_message_id":
            await message.answer(message.chat.id)
        case "/–∏–Ω—Ñ–∞":
            await message.answer("[\n* –ö–ª—É–± –¥–µ–¥–æ–≤-–ø—Ä–æ–≥—Ä–∞–º–º–∏—Å—Ç–æ–≤ FAQ *\n\n–í —ç—Ç–æ–º —Ä–µ–ø–æ–∑–∏—Ç–æ—Ä–∏–∏ –Ω–∞—Ö–æ–¥–∏—Ç—Å—è –ø–æ–ª–µ–∑–Ω–∞—è –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è, —Å–æ–±—Ä–∞–Ω–Ω–∞—è —É—á–∞—Å—Ç–Ω–∏–∫–∞–º–∏ —á–∞—Ç–∞. –î–æ—Ä–æ–∂–Ω—ã–µ –∫–∞—Ä—Ç—ã(roadmaps), —à–ø–∞—Ä–≥–∞–ª–∫–∏, —Å—Å—ã–ª–∫–∏ –Ω–∞ –∫—É—Ä—Å—ã –∏ —Å–∞–º—ã–µ –ø–æ–ª–µ–∑–Ω—ã–µ —Å—Ç–∞—Ç—å–∏.\n\n–≠–ª–µ–º–µ–Ω—Ç–∞—Ä–Ω—ã–π —É—Ä–æ–≤–µ–Ω—å\n - –ù–∞—á–∞–ª–æ —Ä–∞–±–æ—Ç—ã —Å –í–µ–±–æ–º\n - –Ø–∑—ã–∫ –ø—Ä–æ–≥—Ä–∞–º–º–∏—Ä–æ–≤–∞–Ω–∏—è Python\n - –í–≤–µ–¥–µ–Ω–∏–µ –≤ –ø—Ä–æ–≥—Ä–∞–º–º–∏—Ä–æ–≤–∞–Ω–∏–µ. –ü–æ–¥–±–æ—Ä–∫–∞ –º–∞—Ç–µ—Ä–∏–∞–ª–æ–≤ –ø–æ –æ—Å—Ç–∞–ª—å–Ω—ã–º —è–∑—ã–∫–∞–º.\n\n–ü—Ä–æ–¥–≤–∏–Ω—É—Ç—ã–π —É—Ä–æ–≤–µ–Ω—å\n - –í—Å–µ –¥–ª—è –≤–µ–±-—Ä–∞–∑—Ä–∞–±–æ—Ç—á–∏–∫–∞\n - –í—Å–µ –¥–ª—è Python-—Ä–∞–∑—Ä–∞–±–æ—Ç—á–∏–∫–∞\n - –ü–µ—Ç-–ø—Ä–æ–µ–∫—Ç—ã —Ä–∞–∑–Ω–æ–π —Å–ª–æ–∂–Ω–æ—Å—Ç–∏: —Ä–µ—Å—É—Ä—Å—ã, –∏–¥–µ–∏\n - –ó–∞–¥–∞—á–∏ –¥–ª—è –ø—Ä–æ–∫–∞—á–∫–∏ + —Ç–µ—Å—Ç–æ–≤—ã–µ –∑–∞–¥–∞–Ω–∏—è\n\n–ú–∞—Ç–µ—Ä–∏–∞–ª—ã –∏ —Ä–µ—Å—É—Ä—Å—ã –¥—Ä—É–≥–∏—Ö –ò–¢-—Å–æ–æ–±—â–µ—Å—Ç–≤:\n - –ß—Ç–æ —É—á–∏—Ç—å –≤–µ–±-—Ä–∞–∑—Ä–∞–±–æ—Ç—á–∏–∫—É. –†–∞–∑–Ω—ã–µ –≥–∞–π–¥—ã 2021\n - –ö–∞—Ä—Ç—ã —Ä–∞–∑–≤–∏—Ç–∏—è Python 2021\n\n–ü—Ä–æ—Ñ–µ—Å—Å–∏–∏\n - –í —Ç–µ—Å—Ç–∏—Ä–æ–≤—â–∏–∫–∏ —Ö–æ—á—É, –ø—É—Å—Ç—å –º–µ–Ω—è –Ω–∞—É—á–∞—Ç!\n - –ö—Ç–æ —Ç–∞–∫–∏–µ –±–∏–∑–Ω–µ—Å-–∞–Ω–∞–ª–∏—Ç–∏–∫–∏ –∏ –∫–∞–∫ —Å—Ç–∞—Ç—å –ë–ê?\n\n–ü–æ–ª–µ–∑–Ω–æ–µ:\n - Git –∏ GitHub\n - –ö–∞–∫ –º—ã —É—á–∏–º –∞–Ω–≥–ª–∏–π—Å–∫–∏–π —è–∑—ã–∫](https://github.com/OldCodersClub/faq)", parse_mode='markdown', disable_web_page_preview=True)
        case "–∞–ª–∏—Å–∞ —Å–∫–æ–ª—å–∫–æ –≤—Ä–µ–º–µ–Ω–∏?":
            await message.answer(message.from_user.full_name+", [\n* –ö–ª—É–± –¥–µ–¥–æ–≤-–ø—Ä–æ–≥—Ä–∞–º–º–∏—Å—Ç–æ–≤ FAQ *\n\n–í —ç—Ç–æ–º —Ä–µ–ø–æ–∑–∏—Ç–æ—Ä–∏–∏ –Ω–∞—Ö–æ–¥–∏—Ç—Å—è –ø–æ–ª–µ–∑–Ω–∞—è –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è, —Å–æ–±—Ä–∞–Ω–Ω–∞—è —É—á–∞—Å—Ç–Ω–∏–∫–∞–º–∏ —á–∞—Ç–∞. –î–æ—Ä–æ–∂–Ω—ã–µ –∫–∞—Ä—Ç—ã(roadmaps), —à–ø–∞—Ä–≥–∞–ª–∫–∏, —Å—Å—ã–ª–∫–∏ –Ω–∞ –∫—É—Ä—Å—ã –∏ —Å–∞–º—ã–µ –ø–æ–ª–µ–∑–Ω—ã–µ —Å—Ç–∞—Ç—å–∏.\n\n–≠–ª–µ–º–µ–Ω—Ç–∞—Ä–Ω—ã–π —É—Ä–æ–≤–µ–Ω—å\n - –ù–∞—á–∞–ª–æ —Ä–∞–±–æ—Ç—ã —Å –í–µ–±–æ–º\n - –Ø–∑—ã–∫ –ø—Ä–æ–≥—Ä–∞–º–º–∏—Ä–æ–≤–∞–Ω–∏—è Python\n - –í–≤–µ–¥–µ–Ω–∏–µ –≤ –ø—Ä–æ–≥—Ä–∞–º–º–∏—Ä–æ–≤–∞–Ω–∏–µ. –ü–æ–¥–±–æ—Ä–∫–∞ –º–∞—Ç–µ—Ä–∏–∞–ª–æ–≤ –ø–æ –æ—Å—Ç–∞–ª—å–Ω—ã–º —è–∑—ã–∫–∞–º.\n\n–ü—Ä–æ–¥–≤–∏–Ω—É—Ç—ã–π —É—Ä–æ–≤–µ–Ω—å\n - –í—Å–µ –¥–ª—è –≤–µ–±-—Ä–∞–∑—Ä–∞–±–æ—Ç—á–∏–∫–∞\n - –í—Å–µ –¥–ª—è Python-—Ä–∞–∑—Ä–∞–±–æ—Ç—á–∏–∫–∞\n - –ü–µ—Ç-–ø—Ä–æ–µ–∫—Ç—ã —Ä–∞–∑–Ω–æ–π —Å–ª–æ–∂–Ω–æ—Å—Ç–∏: —Ä–µ—Å—É—Ä—Å—ã, –∏–¥–µ–∏\n - –ó–∞–¥–∞—á–∏ –¥–ª—è –ø—Ä–æ–∫–∞—á–∫–∏ + —Ç–µ—Å—Ç–æ–≤—ã–µ –∑–∞–¥–∞–Ω–∏—è\n\n–ú–∞—Ç–µ—Ä–∏–∞–ª—ã –∏ —Ä–µ—Å—É—Ä—Å—ã –¥—Ä—É–≥–∏—Ö –ò–¢-—Å–æ–æ–±—â–µ—Å—Ç–≤:\n - –ß—Ç–æ —É—á–∏—Ç—å –≤–µ–±-—Ä–∞–∑—Ä–∞–±–æ—Ç—á–∏–∫—É. –†–∞–∑–Ω—ã–µ –≥–∞–π–¥—ã 2021\n - –ö–∞—Ä—Ç—ã —Ä–∞–∑–≤–∏—Ç–∏—è Python 2021\n\n–ü—Ä–æ—Ñ–µ—Å—Å–∏–∏\n - –í —Ç–µ—Å—Ç–∏—Ä–æ–≤—â–∏–∫–∏ —Ö–æ—á—É, –ø—É—Å—Ç—å –º–µ–Ω—è –Ω–∞—É—á–∞—Ç!\n - –ö—Ç–æ —Ç–∞–∫–∏–µ –±–∏–∑–Ω–µ—Å-–∞–Ω–∞–ª–∏—Ç–∏–∫–∏ –∏ –∫–∞–∫ —Å—Ç–∞—Ç—å –ë–ê?\n\n–ü–æ–ª–µ–∑–Ω–æ–µ:\n - Git –∏ GitHub\n - –ö–∞–∫ –º—ã —É—á–∏–º –∞–Ω–≥–ª–∏–π—Å–∫–∏–π —è–∑—ã–∫](https://github.com/OldCodersClub/faq)", parse_mode='markdown', disable_web_page_preview=True)
        case "–∞–ª–∏—Å–∞, —Å–∫–æ–ª—å–∫–æ –≤—Ä–µ–º–µ–Ω–∏?":
            await message.answer("–î–µ–¥ "+message.from_user.full_name+", –ø–æ –≥—Ä–∏–Ω–≤–∏—á—É "+datetime.now().strftime("%H —á %M –º–∏–Ω"))
        case "/username":
            # check_language () #message.answer(check_language ())
            await message.answer(message.from_user.full_name)
        case "/userid":
            await message.answer(message.from_user.id)
        case "–∞–ª–∏—Å–∞, —Å–∫–æ–ª—å–∫–æ –≤—Ä–µ–º—è?":
            await message.answer("–≠, —Å–ª—É—à–∞–π, –¥–µ–¥ "++message.from_user.full_name+", —Ç–∞–∫ –Ω–µ–∫—É–ª—å—Ç—É—Ä–Ω–æ –≥–æ–≤–æ—Ä–∏—Ç—å")
        case "–∞–ª–∏—Å–∞, –∫–æ—Ç–æ—Ä—ã–π —á–∞—Å?":
            await message.answer("–î–µ–¥ "+message.from_user.full_name+", —Ç—ã —Ç–∞–∫–æ–π –∫—É–ª—å—Ç—É—Ä–Ω—ã–π. –ü–æ –≥—Ä–∏–Ω–≤–∏—á—É "+datetime.today().strftime("%H —á %M –º–∏–Ω"))
        case "–ø—Ä–∏–≤–µ—Ç, –¥–µ–¥—ã":
            await message.answer("–ò —Ç–æ–±–µ –ø—Ä–∏–≤–µ—Ç, C—Ç–∞—Ä–∏–Ω–∞ "+message.from_user.full_name)
        case "/help":
            await helpComand(message)
        case "–¥—É—à–Ω–∏–ª–∞":
            await message.answer("–≠, –ø–æ—Ä–∏–¥–∂  "+message.from_user.full_name+", —Ç—É—Ç —Ç–µ–±–µ –¥–µ–¥—ã, —Ñ–∏–ª—å—Ç—Ä—É–π –±–∞–∑–∞—Ä ")

    if "–ø—Ä–∏–≤–µ—Ç –¥–µ–¥—ã" in message.text.lower():
        await message.answer("–ò —Ç–æ–±–µ –ø—Ä–∏–≤–µ—Ç, C—Ç–∞—Ä–∏–Ω–∞ "+message.from_user.full_name)
    elif "–≤—Å—Ç—É–ø–∏–ª(–∞) –≤ –≥—Ä—É–ø–ø—É" in message.text.lower():
        await message.answer("–î–æ–±—Ä–æ –ø–æ–∂–∞–ª–æ–≤–∞—Ç—å, "+message.from_user.full_name + ",\n–°–æ–≤–µ—Ç—É–µ–º –æ–∑–Ω–∞–∫–æ–º–∏—Ç—å—Å—è —Å –¥–µ–¥–æ–≤—Å–∫–∏–º –∞—Ä—Ö–∏–≤–æ–º –∑–Ω–∞–Ω–∏–π\n\n\nhttps://github.com/OldCodersClub/faq")
